CREATE TABLE IF NOT EXISTS `cmq` (
    `id` BIGINT UNSIGNED AUTO_INCREMENT NOT NULL,
    `command_class` VARCHAR(255) NOT NULL,
    `command` LONGBLOB NOT NULL,
    `pool` VARCHAR(100) NOT NULL,
    `failed_no` SMALLINT DEFAULT 0 NOT NULL,
    `processed` TINYINT(1) DEFAULT '0' NOT NULL,
    `process_after` DATETIME NOT NULL,
    INDEX IDX_4281F82BAF91A98627FB1B8BDF1863B0 (pool, processed, process_after),
    PRIMARY KEY(id))
DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
